$main-font: #333;
$main-green: #669900;
$main-white: #f6f5f0;
$main-grey: #999;

$sub-grey: #656565;
$sub-green: #64a70b;

$bright-grey: #e5e5e5;
$darker-grey: #2c2a29;

$real-black: #000;
$real-white: #fff;

// COMMON
body {
  color: $main-font;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  font-family: "Nanum Gothic", sans-serif;

  height: 3000px;

  img {
    display: block;
  }
  a {
    text-decoration: none;
  }
  .inner {
    position: relative;
    width: 1100px;
    margin: 0 auto;
  }
}

// COMMON - BTN
.btn {
  $btn-grey: #333;
  $btn-brown: #592b18;
  $btn-gold: #d9aa8a;
  $btn-white: #fff;

  width: 130px;
  padding: 10px;
  border: 2px solid $btn-grey;
  border-radius: 4px;
  color: $btn-grey;
  font-size: 1em;
  font-weight: 700;
  text-align: center;
  cursor: pointer;
  box-sizing: border-box;
  display: block;
  transition: 0.4s;
  &:hover {
    background-color: $btn-grey;
    color: $btn-white;
  }

  // reverse
  &.btn--reverse {
    background-color: $btn-grey;
    color: $btn-white;
    &:hover {
      background-color: transparent;
      color: $btn-grey;
    }
  }

  // brown
  &.btn--brown {
    color: $btn-brown;
    border-color: $btn-brown;
    &:hover {
      color: $btn-white;
      background-color: $btn-brown;
    }
  }

  // gold
  &.btn--gold {
    color: $btn-gold;
    background-color: $btn-gold;
    &:hover {
      color: $btn-white;
      background-color: $btn-gold;
    }
  }

  // white
  &.btn--white {
    color: $btn-white;
    border-color: $btn-white;
    &:hover {
      color: $btn-grey;
      background-color: $btn-white;
    }
  }
}

// HEADER
header {
  position: fixed;
  background-color: $main-white;
  border-bottom: 1px solid #c8c8c8;
  top: 0;
  width: 100%;
  z-index: 10;

  > .inner {
    height: 120px;

    // logo
    .logo {
      height: 75px;
      position: absolute;
      top: 0;
      bottom: 0;
      margin: auto;
    }

    // sub-menu
    .sub-menu {
      position: absolute;
      top: 10px;
      right: 0;
      display: flex;
      .menu {
        font-family: Arial, Helvetica, sans-serif;
        display: flex;
        li {
          position: relative;
          a {
            font-size: 0.75em;
            padding: 11px 16px;
            display: block;
            color: $sub-grey;
            &:hover {
              color: $real-black;
            }
          }
          &:first-child::before {
            display: none;
          }
          &::before {
            position: absolute;
            content: "";
            width: 1px;
            height: 0.75em;
            background-color: $bright-grey;
            top: 0;
            bottom: 0;
            margin: auto;
          }
        }
      }
      .search {
        position: relative;
        height: 34px;
        input {
          width: 36px;
          height: 34px;
          padding: 4px 10px;
          border: 1px solid #ccc;
          box-sizing: border-box;
          border-radius: 5px;
          outline: none;
          background-color: $real-white;
          color: $sub-grey;
          font-size: 0.75em;
          transition: width 0.4s;
          &:focus {
            width: 190px;
            border-color: $main-green;
          }
        }
        .material-icons {
          height: 24px;
          position: absolute;
          top: 0;
          bottom: 0;
          right: 5px;
          margin: auto;
          transition: 0.4s;
        }
        &.focused {
          .material-icons {
            opacity: 0;
          }
        }
      }
    }

    // main-menu
    .main-menu {
      display: flex;
      position: absolute;
      bottom: 0;
      right: 0;
      z-index: 1;
      .item {
        .item__name {
          padding: 10px 20px 34px 20px;
          font: {
            family: Arial, sans-serif;
            size: 13px;
          }
          &:hover {
            cursor: pointer;
            background-color: $darker-grey;
            color: $main-green;
            border-radius: 6px 6px 0 0;
          }
        }
        .item__contents {
          width: 100%;
          position: fixed;
          left: 0;
          display: none;
          .contents__menu {
            background-color: $darker-grey;
            > ul {
              display: flex;
              padding: 20px 0;
              > li {
                width: 220px;
                h4 {
                  padding: 3px 0 12px 0;
                  font-size: 14px;
                  color: $real-white;
                }
                ul {
                  li {
                    padding: 5px 0;
                    font-size: 0.75em;
                    color: $main-grey;
                    cursor: pointer;
                    &:hover {
                      color: $main-green;
                    }
                  }
                }
              }
            }
          }
          .contents__texture {
            background-image: url("../images/main_menu_pattern.jpg");
            padding: 26px 0;
            font-size: 0.75em;
            h4 {
              color: $main-grey;
              font-weight: 700;
            }
            p {
              color: $main-green;
              margin: 4px 0 14px;
            }
          }
        }
        &:hover {
          .item__contents {
            display: block;
          }
        }
      }
    }
  }

  // badge
  .badges {
    position: absolute;
    top: 132px;
    right: 12px;
    .badge {
      border-radius: 10px;
      overflow: hidden;
      margin-bottom: 12px;
      box-shadow: 4px 4px 10px rgba($color: $real-black, $alpha: 0.15);
      cursor: pointer;
    }
  }
}

// VISUAL
.visual {
  margin-top: 120px;
  background-image: url("../images/visual_bg.jpg");
  background-position: center;
  .inner {
    height: 646px;
    // title
    .title {
      position: absolute;
      top: 88px;
      left: -10px;
      .btn {
        position: absolute;
        top: 259px;
        left: 173px;
      }
    }

    // image
    .cup1 {
      &.image {
        position: absolute;
        bottom: 0;
        right: -47px;
      }
      &.text {
        position: absolute;
        top: 38px;
        right: 171px;
      }
    }
    .cup2 {
      &.image {
        position: absolute;
        bottom: 0;
        right: 162px;
      }
      &.text {
        position: absolute;
        top: 321px;
        right: 416px;
      }
    }
    .spoon {
      position: absolute;
      bottom: 0px;
      right: 275px;
    }
  }

  // fade in
  .fade-in {
    opacity: 0;
  }
}

// NOTICE
.notice {
  .notice-line {
    position: relative;
    .bg-left {
      position: absolute;
      top: 0;
      left: 0;
      width: 50%;
      height: 100%;
      background-color: $main-font;
    }
    .bg-right {
      position: absolute;
      top: 0;
      right: 0;
      width: 50%;
      height: 100%;
      background-color: $main-white;
    }
    .inner {
      height: 62px;
      display: flex;

      .inner__left {
        width: 60%;
        height: 100%;
        background-color: $main-font;
        display: flex;
        align-items: center;
        h2 {
          color: $real-white;
          font-size: 1.1em;
          font-weight: 700;
          margin-right: 20px;
        }
        .swiper-container {
          flex-grow: 1;
          height: 62px;
          .swiper-slide {
            a {
              color: $real-white;
            }
          }
        }
        .notice-line__more {
          display: flex;
          width: 62px;
          height: 62px;
          justify-content: center;
          align-items: center;
          .material-icons {
            color: $real-white;
            font-size: 1.875em;
          }
        }
      }

      .inner__right {
        width: 40%;
        height: 100%;
        display: flex;
        justify-content: flex-end;
        align-items: center;
        h2 {
          font-size: 1.1em;
          font-weight: 700;
        }
        .toggle-promotion {
          width: 62px;
          height: 62px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          .material-icons {
            font-size: 1.875em;
          }
        }
      }
    }
  }
}
